'use strict';

var kraken = require('kraken-js');
var express = require('express');

function createApp() {
  var app = express();

  app.use(kraken());

  app.on('start', function () {
    console.log('subapp started');
  });

  return app;
};

if (require.main === module) {
  createApp().listen(8001, function () {
    console.log('subapp bound to 8001');
  });
} else {
  module.exports = createApp;
}
